import React, { Component } from 'react';

export default class Main extends Component {
    render() {

        const {currentNote} = this.props
       
            if(!currentNote) {
                return (
                <div className="main">
                    <p>No Active Notes</p>
                </div>
                )
            } else {
                console.log(currentNote)
                var allNotes = JSON.parse(localStorage.getItem("allNotes"));
                const title = (currentNote) => 
                {
                  const currentNoteTitle =  allNotes.filter(note => currentNote === note.id)

                }
                return (
                    <div className="form">
                    <form>
                        <input type="text" id="title" name="title" value={currentNote.title} /> <br /> <br />
                        <textarea id="description" name="description" placeholder="Your note..."></textarea>
                    </form>
                    </div>
                )
            }
           
        
    }
}
