import './App.css';
import Main from './Main';
import react, {Component} from 'react'
import Sidebar from './Sidebar';
import uuid from 'react-uuid';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentNote: false
    }
    this.updateNote =  this.updateNote.bind(this);
    this.updateChanges =  this.updateChanges.bind(this);
  }

  updateChanges(updatedNote) {
    var allNotes = JSON.parse(localStorage.getItem("allNotes"));
    if(allNotes == null) allNotes=[];
    allNotes.push(updatedNote[0]);
    localStorage.setItem('allNotes', JSON.stringify(allNotes));
  }

  updateNote(event) {
    let name = event.target.name;
    let value = event.target.value;

    var allNotes = JSON.parse(localStorage.getItem("allNotes"));

      const current =  allNotes.filter(note => this.state.currentNote === note.id)
      updateChanges({...current, [name]: value, lastEdited: Date.now()} )

  }


  render() {
    const addNote = () => {
      const note = {
        id: uuid(),
        title: "",
        description: "",
        lastEdited: Date.now()
      }

      var allNotes = JSON.parse(localStorage.getItem("allNotes"));
      if(allNotes == null) allNotes=[];
      allNotes.push(note);
      localStorage.setItem('allNotes', JSON.stringify(allNotes));
      this.setState ({
        currentNote: note.id
      })
      console.log(this.state.currentNote);

    }



    return (
      <div className="App">
        <Sidebar addNote={addNote}/>
        <Main currentNote={this.state.currentNote} updateNote={this.updateNote}/>
      </div>
    );
  }

}

export default App;
