import React, { Component } from 'react';

export default class Main extends Component {
    render() {

        const {currentNote, updateNote} = this.props
       
            if(!currentNote) {
                return (
                <div className="main">
                    <p>No Active Notes</p>
                </div>
                )
            } else {
                console.log(currentNote)
                var allNotes = JSON.parse(localStorage.getItem("allNotes"));
                const title = (id) => 
                {
                  const current =  allNotes.filter(note => id === note.id)
                  return current[0].title

                }

                return (
                    <div className="form">
                    <form>
                        <input type="text" id="title" name="title" value= {title(currentNote)} onChange={updateNote} autoFocus /> <br /> <br />
                        <textarea id="description" name="description" placeholder="Your note..."></textarea>
                    </form>
                    </div>
                )
            }
           
        
    }
}
