import React, { Component } from 'react';
import Notes from './Notes';

export default class Sidebar extends Component {
    render() {
        
        const {addNote, handleDelete} = this.props;
        var allNotes = JSON.parse(localStorage.getItem("allNotes"));
        if(allNotes === null) {
                return (
                    <div className="sidebar">
                    <span className="header">Notes</span>
                    <i className="material-icons" onClick={addNote}>add_box</i>
                    <hr/>
                    {/* <Notes handleDelete={handleDelete} /> */}
                    </div>
                );
        } else {
            allNotes.map(note => {
                return(
                     <div>
                         <ul class="collection">
                             <li class="collection-item avatar">
                                 <span className="title" >{note.title}</span>
                                 <p>Last Modified{" "}
                                   {new Date(note.lastEdited).toLocaleDateString("en-GB", {
                                    hour: "2-digit",
                                    minute: "2-digit",
                                  })}</p>
                                 <div className="secondary-content">
                                     // eslint-disable-next-line jsx-a11y/anchor-is-valid
                                     <a href="" style={{color: 'red'}} onClick={()  => handleDelete(note.id)} ><i class="material-icons">delete</i></a>
                                </div>  
                            </li>
                        </ul>
                         
                     <hr />
                     </div>              
                );
            })
        }
    }
}
