import React, { Component } from 'react'

export default class Notes extends Component {
    render() {
        var allNotes = JSON.parse(localStorage.getItem("allNotes"));
        if(allNotes === null) return <div></div>
        return (
           
            allNotes.map(note => {
                return(
                     <div>
                         <ul class="collection">
                             <li class="collection-item avatar">
                                 <span className="title" >{note.title}</span>
                                 <p style={{
  textDecoration: isStrike(item.id) ? 'line-through' : 'none'
}}>{item.description}</p>
                                 <div className="secondary-content">
                                     <a href="#" style={{marginRight: '25px', color: 'green', display: isStrike(item.id) ? 'none' : ''}} onClick={() => handleComplete(item.id)}><i class="material-icons">done</i></a>
                                     <a href="#" style={{color: 'red'}} onClick={() => handleDelete(item.id)} ><i class="material-icons">delete</i></a>
                                </div>
                            </li>
                        </ul>
                         <h3>{note.title}</h3>
                         
                         
                         <p>Last Modified{" "}
              {new Date(note.lastEdited).toLocaleDateString("en-GB", {
                hour: "2-digit",
                minute: "2-digit",
              })}</p>
              <i class="material-icons" style={{color: 'red', fontSize: '30px'}}>delete</i>
              <hr />
                     </div>              
                );
            })
        )
    }
}
