/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react';



export default class Sidebar extends Component {

    render() {
        function notes(){
            var allNotes = JSON.parse(localStorage.getItem("allNotes"));
            allNotes.map(note => {
                return(
                     <div>
                         <ul class="collection">
                             <li class="collection-item avatar">
                                 <span className="title" >{note.title}</span>
                                 <p>Last Modified{" "}
                                   {new Date(note.lastEdited).toLocaleDateString("en-GB", {
                                    hour: "2-digit",
                                    minute: "2-digit",
                                  })}</p>
                                 <div className="secondary-content">
                                     <a href="" style={{color: 'red'}} ><i class="material-icons">delete</i></a>
                                </div>  
                            </li>
                        </ul>
                         
                     <hr />
                     </div>              
                );
            })
        }
        
        
        const {addNote} = this.props;
                return (
                    <div className="sidebar">
                    <span className="header">Notes</span>
                    <i className="material-icons" onClick={addNote}>add_box</i>
                    <hr/>
                   {notes()}
                    </div>
                );
        
    }
}
